fn main(){let s=std::env::args().nth(1).unwrap();match if s.starts_with("0x"){i32::from_str_radix(&s[2..],16)}else{s.parse()}{Ok(mut n)=>{let mut o=String::new();while n>0{n-=1;o.insert(0,"▖▘▌".chars().nth((n%3)as usize).unwrap());n/=3}print!("{o}\n")},_=>{let mut t=s.chars().rev().map(|c|match c{'▖'=>1,'▘'=>2,'▌'=>3,_=>0}).fold(0,|a,d|a*4+d);let mut n=0;while t>0{n=n*3+t%4;t/=4}print!("{n} {n:#x}\n")}}}
