fn main(){for s in std::env::args().skip(1){println!("{}",match if s.starts_with("0x"){usize::from_str_radix(&s[2..],16)}else{s.parse()}{Ok(mut n)=>{let mut o=String::new();while n>0{n-=1;o.insert(0,['▖','▘','▌'][n%3]);n/=3}o}_=>{let n=s.chars().fold(0,|a,d|a*3+(d as u32>>2&3));format!("{n} {n:#x}")}})}}
